// üîê SECURITY CHECK: API KEY + RATE LIMITING
// –ù–∞—Å—Ç—Ä–æ–π–∫–∏ –±–µ—Ä—É—Ç—Å—è –∏–∑ —É–∑–ª–∞ CONFIG
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

const config = $('‚öôÔ∏è CONFIG').item.json;
const API_KEY = config.api_key;
const RATE_LIMIT_WINDOW_MS = config.rate_limit_window_ms;
const RATE_LIMIT_MAX_REQUESTS = config.rate_limit_max;

 const inputData = $('üåê Webhook: Analyze Dialog').item.json;
const body = inputData.body || {};
const headers = inputData.headers || {};
const query = inputData.query || {};
const clientIP = headers['x-real-ip'] || headers['x-forwarded-for'] || headers['cf-connecting-ip'] || 'unknown';
const now = Date.now();

// RATE LIMITING
const staticData = $getWorkflowStaticData('global');
if (!staticData.rateLimits) { staticData.rateLimits = {}; }
for (const ip in staticData.rateLimits) {
  if (now - staticData.rateLimits[ip].firstRequest > RATE_LIMIT_WINDOW_MS) {
    delete staticData.rateLimits[ip];
  }
}
if (!staticData.rateLimits[clientIP]) {
  staticData.rateLimits[clientIP] = { count: 1, firstRequest: now };
} else {
  staticData.rateLimits[clientIP].count++;
  if (staticData.rateLimits[clientIP].count > RATE_LIMIT_MAX_REQUESTS) {
    return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Too many requests', details: `Limit: ${RATE_LIMIT_MAX_REQUESTS} requests per minute`, status: 429, retryAfter: Math.ceil((staticData.rateLimits[clientIP].firstRequest + RATE_LIMIT_WINDOW_MS - now) / 1000) } } }];
  }
}

// API KEY CHECK
const providedKey = headers['x-api-key'] || body.apiKey || query.apiKey || '';
if (!providedKey) {
  return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'API key not provided', status: 401 } } }];
}
if (providedKey !== API_KEY) {
  return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Invalid API key', status: 401 } } }];
}

return [{ json: { ...inputData, authorized: true } }];

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// üìù PROCESS REQUEST
// –ò–∑–≤–ª–µ—á–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –∏–∑ –∑–∞–ø—Ä–æ—Å–∞
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

const requestData = items[0].json.body || items[0].json;
const analysisType = requestData.type;
const sessionId = requestData.sessionId;
const language = requestData.language;

return [{
    json: {
        type: analysisType,
        sessionId: sessionId,
        language: language,
        userName: requestData.userName
    }
}];

