// –û–±—ä–µ–¥–∏–Ω–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –∑–∞–ø—Ä–æ—Å–∞ —Å —è–∑—ã–∫–æ–º –∞–Ω–∞–ª–∏–∑–∞
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

const requestData = $('üìù Process Request').first().json;
const langSetting = items[0]?.json?.language_code || 'ru';

return [{
    json: {
        type: requestData.type,
        sessionId: requestData.sessionId,
        language: requestData.language,
        userName: requestData.userName,
        analysisLanguage: langSetting
    }
}];

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// üîÄ MERGE CHECK DATA
// –û–±—ä–µ–¥–∏–Ω–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –ø—Ä–æ–≤–µ—Ä–∫–∏ —Å –¥–∞–Ω–Ω—ã–º–∏ –∑–∞–ø—Ä–æ—Å–∞
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

const checkResult = items[0].json;
const mergedData = $('üîÄ Merge with Language').first().json;

const parseJsonField = (field) => {
    if (!field) return null;
    if (typeof field === 'string') {
        try {
            return JSON.parse(field);
        } catch (e) {
            return field;
        }
    }
    return field;
};

const previousAnalysis = checkResult.last_analysis_date ? {
    emotionalTone: parseJsonField(checkResult.prev_emotional_tone),
    customerNeeds: parseJsonField(checkResult.prev_customer_needs),
    missedOpportunities: parseJsonField(checkResult.prev_missed_opportunities),
    recommendations: parseJsonField(checkResult.prev_recommendations),
    leadScoring: parseJsonField(checkResult.prev_lead_scoring),
    bant: parseJsonField(checkResult.prev_bant),
    entities: parseJsonField(checkResult.prev_entities),
    statistics: parseJsonField(checkResult.prev_statistics),
    satisfaction: checkResult.prev_satisfaction,
    analysisDate: checkResult.last_analysis_date
} : null;

return [{
    json: {
        type: mergedData.type,
        sessionId: mergedData.sessionId,
        language: mergedData.language,
        userName: mergedData.userName,
        analysisLanguage: mergedData.analysisLanguage,
        has_new_messages: checkResult.has_new_messages,
        new_messages_count: checkResult.new_messages_count,
        last_analysis_date: checkResult.last_analysis_date,
        previousAnalysis: previousAnalysis
    }
}];

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// üìä FORMAT EXISTING ANALYSIS
// –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Å—É—â–µ—Å—Ç–≤—É—é—â–µ–≥–æ –∞–Ω–∞–ª–∏–∑–∞ –¥–ª—è –≤–æ–∑–≤—Ä–∞—Ç–∞
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

const existingAnalysis = items[0].json;
const checkData = $('üîÄ Merge Check Data').first().json;

return [{
    json: {
        emotionalTone: existingAnalysis.emotional_tone,
        customerNeeds: existingAnalysis.customer_needs,
        missedOpportunities: existingAnalysis.missed_opportunities,
        recommendations: existingAnalysis.recommendations,
        entities: existingAnalysis.extracted_entities,
        bant: existingAnalysis.bant_qualification,
        leadScoring: existingAnalysis.lead_scoring,
        statistics: existingAnalysis.statistics,
        _cached: true,
        _lastAnalysisDate: checkData.last_analysis_date,
        _newMessagesCount: 0
    }
}];

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// üìä FORMAT DIALOGS
// –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–∏–∞–ª–æ–≥–æ–≤ –¥–ª—è –∞–Ω–∞–ª–∏–∑–∞ –ò–ò
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

const dialogs = items;
const mergedData = $('üîÄ Merge Check Data').first().json;
const analysisType = mergedData.type;
const analysisLanguage = mergedData.analysisLanguage || 'ru';
const lastAnalysisDate = mergedData.last_analysis_date;
const previousAnalysis = mergedData.previousAnalysis;

const sessions = {};

dialogs.forEach(item => {
    const data = item.json;
    if (!sessions[data.session_id]) {
        sessions[data.session_id] = {
            messages: [],
            allMessages: [],
            language: data.language,
            platform: data.platform
        };
    }

    const message = {
        type: data.message_type,
        content: data.content,
        timestamp: data.timestamp
    };

    sessions[data.session_id].allMessages.push(message);

    if (lastAnalysisDate && lastAnalysisDate !== 'null' && lastAnalysisDate !== '') {
        const lastAnalysisTime = new Date(lastAnalysisDate).getTime();
        const msgTime = new Date(data.timestamp).getTime();
        if (msgTime > lastAnalysisTime) {
            sessions[data.session_id].messages.push(message);
        }
    } else {
        sessions[data.session_id].messages.push(message);
    }
});

const dialogsForAnalysis = Object.entries(sessions).map(([sessionId, data]) => {
    const clientLabel = analysisLanguage === 'en' ? 'Client' : 
                        analysisLanguage === 'es' ? 'Cliente' : 
                        analysisLanguage === 'fr' ? 'Client' : 
                        analysisLanguage === 'de' ? 'Kunde' : 
                        analysisLanguage === 'it' ? 'Cliente' : 
                        analysisLanguage === 'pt' ? 'Cliente' : 
                        analysisLanguage === 'zh' ? 'ÂÆ¢Êà∑' : 
                        analysisLanguage === 'ja' ? 'È°ßÂÆ¢' : 
                        analysisLanguage === 'ko' ? 'Í≥†Í∞ù' : 
                        analysisLanguage === 'ua' ? '–ö–ª—ñ—î–Ω—Ç' : 
                        '–ö–ª–∏–µ–Ω—Ç';

    const botLabel = analysisLanguage === 'en' ? 'Bot' : 
                     analysisLanguage === 'es' ? 'Bot' : 
                     analysisLanguage === 'fr' ? 'Bot' : 
                     analysisLanguage === 'de' ? 'Bot' : 
                     analysisLanguage === 'it' ? 'Bot' : 
                     analysisLanguage === 'pt' ? 'Bot' : 
                     analysisLanguage === 'zh' ? 'Êú∫Âô®‰∫∫' : 
                     analysisLanguage === 'ja' ? '„Éú„ÉÉ„Éà' : 
                     analysisLanguage === 'ko' ? 'Î¥á' : 
                     analysisLanguage === 'ua' ? '–ë–æ—Ç' : 
                     '–ë–æ—Ç';

    const conversation = data.messages.map(msg => 
        `${msg.type === 'human' ? clientLabel : botLabel}: ${msg.content}`
    ).join('\n');

    return {
        sessionId: sessionId,
        language: data.language,
        platform: data.platform,
        conversation: conversation,
        newMessageCount: data.messages.length,
        totalMessageCount: data.allMessages.length
    };
});

const isUpdate = !!previousAnalysis;

return [{
    json: {
        type: analysisType,
        dialogs: dialogsForAnalysis,
        totalDialogs: dialogsForAnalysis.length,
        analysisLanguage: analysisLanguage,
        isUpdate: isUpdate,
        previousAnalysis: previousAnalysis
    }
}];

