// ============================================
// ğŸ” UNIVERSAL PROTECTION: API KEY + RATE LIMITING
// ============================================

// Get settings from CONFIG
const config = $('âš™ï¸ CONFIG').first().json;
const API_KEY = config.api_key;
const RATE_LIMIT_WINDOW_MS = config.rate_limit_window_ms;
const RATE_LIMIT_MAX_REQUESTS = config.rate_limit_max_requests;

const inputData = $('ğŸŒ Webhook: Get Single Analysis').item.json;
const body = inputData.body || {};
const headers = inputData.headers || {};
const query = inputData.query || {};
const clientIP = headers['x-real-ip'] || headers['x-forwarded-for'] || headers['cf-connecting-ip'] || 'unknown';
const now = Date.now();

// RATE LIMITING
const staticData = $getWorkflowStaticData('global');
if (!staticData.rateLimits) { staticData.rateLimits = {}; }
for (const ip in staticData.rateLimits) {
  if (now - staticData.rateLimits[ip].firstRequest > RATE_LIMIT_WINDOW_MS) {
    delete staticData.rateLimits[ip];
  }
}
if (!staticData.rateLimits[clientIP]) {
  staticData.rateLimits[clientIP] = { count: 1, firstRequest: now };
} else {
  staticData.rateLimits[clientIP].count++;
  if (staticData.rateLimits[clientIP].count > RATE_LIMIT_MAX_REQUESTS) {
    return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Too many requests', details: `Limit: ${RATE_LIMIT_MAX_REQUESTS} requests per minute`, status: 429, retryAfter: Math.ceil((staticData.rateLimits[clientIP].firstRequest + RATE_LIMIT_WINDOW_MS - now) / 1000) } } }];
  }
}

// API KEY CHECK
const providedKey = headers['x-api-key'] || body.apiKey || query.apiKey || '';
if (!providedKey) {
  return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'API key not provided', status: 401 } } }];
}
if (providedKey !== API_KEY) {
  return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Invalid API key', status: 401 } } }];
}

return [{ json: { ...inputData, authorized: true } }];

const data = items[0]?.json;
if (!data) {
  return [{json: {found: false, message: 'Analysis not found'}}];
}

// Parse JSONB fields
let emotionalTone = data.emotional_tone;
let customerNeeds = data.customer_needs;
let missedOpportunities = data.missed_opportunities;
let recommendations = data.recommendations;
let statistics = data.statistics;
let leadScoring = data.lead_scoring;

// Check and parse if strings
if (typeof emotionalTone === 'string') {
  try { emotionalTone = JSON.parse(emotionalTone); } catch(e) {}
}
if (typeof customerNeeds === 'string') {
  try { customerNeeds = JSON.parse(customerNeeds); } catch(e) {}
}
if (typeof missedOpportunities === 'string') {
  try { missedOpportunities = JSON.parse(missedOpportunities); } catch(e) {}
}
if (typeof recommendations === 'string') {
  try { recommendations = JSON.parse(recommendations); } catch(e) {}
}
if (typeof statistics === 'string') {
  try { statistics = JSON.parse(statistics); } catch(e) {}
}
if (typeof leadScoring === 'string') {
  try { leadScoring = JSON.parse(leadScoring); } catch(e) {}
}

return [{
  json: {
    found: true,
    email: data.email,
    threadId: data.thread_id,
    userName: data.user_name,
    analysisType: data.analysis_type,
    language: data.language,
    emotionalTone: emotionalTone,
    customerNeeds: customerNeeds,
    missedOpportunities: missedOpportunities,
    recommendations: recommendations,
    statistics: statistics,
    satisfactionPercentage: data.satisfaction_percentage || emotionalTone?.satisfaction || 0,
    leadScoring: leadScoring,
    createdAt: data.created_at,
    updatedAt: data.updated_at
  }
}];

// ============================================
// ğŸ” UNIVERSAL PROTECTION: API KEY + RATE LIMITING
// ============================================

// Get settings from CONFIG
const config = $('âš™ï¸ CONFIG').first().json;
const API_KEY = config.api_key;
const RATE_LIMIT_WINDOW_MS = config.rate_limit_window_ms;
const RATE_LIMIT_MAX_REQUESTS = config.rate_limit_max_requests;

const inputData = $('ğŸŒ Webhook: Get All Analysis').item.json;
const body = inputData.body || {};
const headers = inputData.headers || {};
const query = inputData.query || {};
const clientIP = headers['x-real-ip'] || headers['x-forwarded-for'] || headers['cf-connecting-ip'] || 'unknown';
const now = Date.now();

// RATE LIMITING
const staticData = $getWorkflowStaticData('global');
if (!staticData.rateLimits) { staticData.rateLimits = {}; }
for (const ip in staticData.rateLimits) {
  if (now - staticData.rateLimits[ip].firstRequest > RATE_LIMIT_WINDOW_MS) {
    delete staticData.rateLimits[ip];
  }
}
if (!staticData.rateLimits[clientIP]) {
  staticData.rateLimits[clientIP] = { count: 1, firstRequest: now };
} else {
  staticData.rateLimits[clientIP].count++;
  if (staticData.rateLimits[clientIP].count > RATE_LIMIT_MAX_REQUESTS) {
    return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Too many requests', details: `Limit: ${RATE_LIMIT_MAX_REQUESTS} requests per minute`, status: 429, retryAfter: Math.ceil((staticData.rateLimits[clientIP].firstRequest + RATE_LIMIT_WINDOW_MS - now) / 1000) } } }];
  }
}

// API KEY CHECK
const providedKey = headers['x-api-key'] || body.apiKey || query.apiKey || '';
if (!providedKey) {
  return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'API key not provided', status: 401 } } }];
}
if (providedKey !== API_KEY) {
  return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Invalid API key', status: 401 } } }];
}

return [{ json: { ...inputData, authorized: true } }];

const analyses = {};

items.forEach(item => {
  const data = item.json;

  // Parse JSONB fields
  let emotionalTone = data.emotional_tone;
  let leadScoring = data.lead_scoring;

  if (typeof emotionalTone === 'string') {
    try { emotionalTone = JSON.parse(emotionalTone); } catch(e) {}
  }
  if (typeof leadScoring === 'string') {
    try { leadScoring = JSON.parse(leadScoring); } catch(e) {}
  }

  analyses[data.email] = {
    emotionalTone: emotionalTone?.overall || 'unknown',
    satisfactionPercentage: data.satisfaction_percentage || emotionalTone?.satisfaction || 0,
    leadScoring: leadScoring?.score || 0,
    leadTemperature: leadScoring?.temperature || 'cold',
    createdAt: data.created_at,
    updatedAt: data.updated_at
  };
});

return [{json: {analyses}}];
