const aiResponse = $input.first().json;
const historyData = $json;
const clientEmail = $('ğŸ“œ Get History').first().json.email;

const retryCount = historyData.retryCount || 0;
if (retryCount > 2) {
  return [];
}

let emailContent = '';
let shouldSendReply = true;
let replyData = {};

try {
  let output = aiResponse.output;

  if (typeof output === 'string') {
    const trimmedOutput = output.trim();

    if (trimmedOutput.startsWith('<!DOCTYPE') || trimmedOutput.startsWith('<html')) {
      emailContent = trimmedOutput;
    } else {
      try {
        const cleanJson = trimmedOutput.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
        const parsedData = JSON.parse(cleanJson);

        if (parsedData.replyData && parsedData.replyData.emailContent) {
          emailContent = parsedData.replyData.emailContent;
          replyData = parsedData;
        } else if (parsedData.emailContent) {
          emailContent = parsedData.emailContent;
          replyData = parsedData;
        }

        if (parsedData.shouldReply === false) {
          shouldSendReply = false;
        }

      } catch (parseError) {
        if (trimmedOutput.includes('<!DOCTYPE') || trimmedOutput.includes('<html')) {
          const htmlMatch = trimmedOutput.match(/<!DOCTYPE[\s\S]*<\/html>/);
          if (htmlMatch) {
            emailContent = htmlMatch[0];
          }
        }
      }
    }
  } else if (typeof output === 'object') {
    replyData = output;
    if (replyData.replyData && replyData.replyData.emailContent) {
      emailContent = replyData.replyData.emailContent;
    } else if (replyData.emailContent) {
      emailContent = replyData.emailContent;
    }
  }

} catch (e) {
  return [];
}

if (!shouldSendReply) {
  return [];
}

if (!emailContent || !emailContent.includes('<')) {
  return [];
}

if (!emailContent || emailContent.length < 50) {
  return [{
    json: {
      needsRetry: true,
      email: clientEmail,
      retryCount: retryCount + 1,
      ...historyData
    }
  }];
}

return [{
  json: {
    to: clientEmail,
    subject: $('ğŸ”„ Loop Over Items').first().json.subject,
    emailContent: emailContent,
    language: replyData.replyData?.language || historyData.language || 'en',
    attachments: replyData.replyData?.attachments || [],
    threadId: historyData.thread_id,
    messageId: historyData.messageId,
    originalBody: historyData.conversation_history || '',
    senderName: historyData.full_name || 'Client',
    urgency: replyData.replyData?.urgency || 'medium',
    leadScore: replyData.clientData?.leadScore || historyData.lead_score || 0,
    intent: replyData.clientData?.intent || 'information',
    internalNote: replyData.internalNote || '',
    followUpDays: replyData.replyData?.followUpDays || 3
  }
}];

