const emails = $input.all();
const processedEmails = [];

for (const item of emails) {
  const email = item.json;

  const headers = email.payload?.headers || [];
  const from = headers.find(h => h.name === 'From')?.value || email.From || '';
  const subject = headers.find(h => h.name === 'Subject')?.value || email.Subject || '';
  const messageId = email.id;
  const threadId = email.threadId;

  let senderEmail = '';
  let senderName = '';

  const emailMatch = from.match(/<([^>]+)>/) || from.match(/([^\s]+@[^\s]+)/);
  if (emailMatch) {
    senderEmail = emailMatch[1].toLowerCase().trim();
  }

  if (from.includes('<')) {
    senderName = from.split('<')[0].trim().replace(/['"]/g, '');
  } else {
    senderName = from.split('@')[0];
  }

  let body = email.snippet || '';

  let skipReason = null;

  if (senderEmail.includes('noreply') || senderEmail.includes('no-reply')) {
    skipReason = 'NoReply address';
  } else if (senderEmail.includes('facebook')) {
    skipReason = 'Facebook';
  } else if (senderEmail.includes('workspace@google')) {
    skipReason = 'Google Workspace';
  } else if (senderEmail.includes('cryptomator.pro')) {
    const isReply = subject && subject.toLowerCase().includes('re:');
    if (!isReply) {
      skipReason = 'Our notification';
    }
  } else if (!body || body.length < 3) {
    skipReason = 'Empty email';
  }

  if (skipReason) {
    continue;
  }

  const hasRussian = /[Ğ°-ÑĞ-Ğ¯Ñ‘Ğ]/.test(body + subject);
  const language = hasRussian ? 'ru' : 'en';

  processedEmails.push({
    messageId: messageId,
    threadId: threadId,
    from: senderEmail,
    senderName: senderName || 'Client',
    subject: subject || 'No subject',
    body: body.substring(0, 3000),
    language: language,
    timestamp: new Date(parseInt(email.internalDate || Date.now())).toISOString()
  });
}

if (processedEmails.length === 0) {
  return [];
}

return processedEmails.map(e => ({json: e}));

